package operators;

public class Logicalop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int age=18;
		  char bldg='A';
		  boolean a,b;
		  a=(age>18);
		  b=(bldg=='A');
		  
		  System.out.println(a&&b);
		  System.out.println(a||b);
		  System.out.println(!(a&&b));
		  
		
		
		
		
		
		
		
		
		
		
		

	}

}
