package practice;

public class While_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         
		int i=0;
		while(i<5) {
			System.out.println("hello "+i);
			i++;
		}
		int a=5;
		while (a>0) {
		System.out.println(a+" bye");
		a--;
		}
		
		char c1='a';
		while(c1<='z') {
			System.out.print(c1+"  ");
			c1++;
		}
		
		
		
	}

}
